mapper.select:
SELECT EQUIPMENT_CODE, TPTB, EQUIPMENT_STATUS, EQUIPMENT_MODEL_ID, (SELECT EQUIPMENT_NAME FROM EQUIPMENT_MODEL B WHERE A.EQUIPMENT_MODEL_ID = B.EQUIPMENT_MODEL_ID) EQUIPMENT_NAME, REGULAR_INSPECTION, REGULAR_INSPECTION_DATE, PURCHASE_PRICE, (SELECT EQUIPMENT_TYPE FROM EQUIPMENT_MODEL B WHERE A.EQUIPMENT_MODEL_ID = B.EQUIPMENT_MODEL_ID) EQUIPMENT_TYPE, PURCHASE_DATETIME, S_ID, (SELECT S_NAME FROM STORE B WHERE A.S_ID = B.S_ID) S_NAME FROM EQUIPMENT A
WHERE EQUIPMENT_MODEL_ID LIKE CONCAT('%',#{equipmentModelId},'%')
ORDER BY EQUIPMENT_CODE DESC LIMIT #{skip} , #{perSheet}

mapper.insert:
INSERT INTO STORE_EQUIPMENT_PLACEMENT
(
	S_ID
	, EQUIPMENT_CODE
	, X_COORD
	, Y_COORD
)
VALUES(
	#{sId}
	, #{equipmentCode}
	, #{xCoord}
	, #{yCoord}
)
